query = function() {
	Ext.Ajax.request({
				url : 'AjaxPortal',
				callback : function() {
					Ext.Msg.hide();
					//Ext.getCmp('DetilPanel').setDisabled(false);
				},
				success : function(response) {
					var json = parserJSON(response.responseText);
					var msg="";
					for (var index in json['responseData']){
						msg+=index+". "+Ext.encode(json['responseData'][index])+"<br />";
					}
					Ext.get("info").innerHTML=msg;
					info.innerHTML=msg;
					alert(json['responseData'][0]);
				},
				failure : function(response) {
					Ext.Msg.alert("錯誤", "請向程式開發者詢問");
				},
				params : {
					APP_ID : appId,
					ACTION : "GET_ACCOUNT_STATUS",
					RETURN_TYPE : "JSON",
					MODEL_NAME : "ACCOUNT_MANAGE"
				}
			});
	// alert(Ext.getCmp('conditionForm'));
}